import React, { useContext } from "react";
import { CartContext } from "../context/CartContext"; // ✅ Ensure correct import

const CartWidget = () => {
  const cartContext = useContext(CartContext); // ✅ Use a fallback if context is undefined
  if (!cartContext) {
    return null; // ✅ Prevent crashing if CartContext is not provided
  }

  const { cartItems } = cartContext; // ✅ Extract cartItems safely
  const totalItems = cartItems.reduce((total, item) => total + item.quantity, 0);

  return (
    <div>
      <i className="fas fa-shopping-cart"></i>
      <span className="badge bg-secondary">{totalItems}</span> {/* ✅ Show total quantity */}
    </div>
  );
};

export default CartWidget;
