import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import { useCart } from "../context/CartContext";

const CartWidget = () => {
    const { cartItems } = useCart();
    const [isOpen, setIsOpen] = useState(false); // Toggle for the cart dropdown
    const navigate = useNavigate(); // For navigating to the checkout page

    const totalItems = cartItems.reduce((total, item) => total + item.quantity, 0);

    const goToCheckout = () => {
        navigate('/checkout'); // Navigate to the checkout page
    };

    return (
        <div className="cart-widget">
            <div onClick={() => setIsOpen(!isOpen)} className="cart-icon">
                <i className="fas fa-shopping-cart"></i>
                <span className="badge bg-secondary">{totalItems}</span>
            </div>

            {isOpen && (
                <div className="cart-dropdown">
                    {cartItems.length === 0 ? (
                        <p className="empty-cart">Your cart is empty.</p>
                    ) : (
                        <>
                            <ul>
                                {cartItems.map((item) => (
                                    <li key={item.id}>
                                        <img src={item.image} alt={item.name} />
                                        <div>
                                            <p>{item.name}</p>
                                            <p>Qty: {item.quantity}</p>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                            <button className="btn btn-primary" onClick={goToCheckout}>
                                Go to Checkout
                            </button>
                        </>
                    )}
                </div>
            )}
        </div>
    );
};

export default CartWidget;
