import React from 'react';
import coffeeShops from './coffeeShops';  // Assuming the file is named coffeeShops.js and is in the same directory

const Home = () => {
  return (
    <div>
      <h1>Welcome to the Coffee Experience Catalog</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around' }}>
        {coffeeShops.map((shop) => (
          <div key={shop.id} style={{ margin: '20px', border: '1px solid #ccc', padding: '10px', width: '23%' }}>
            <img src={shop.image} alt={shop.name} style={{ width: '100%', height: '200px', objectFit: 'cover' }} />
            <h2>{shop.name}</h2>
            <p>{shop.description}</p>
            <p><strong>Price: ${shop.price}</strong></p>
            {/* Add a Quantity Selector here */}
            <div style={{ marginTop: '10px' }}>
              <label htmlFor={`quantity-${shop.id}`}>Quantity: </label>
              <input type="number" id={`quantity-${shop.id}`} name="quantity" min="1" defaultValue="1" style={{ width: '50px' }} />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
