import React, { useState } from "react";
import { getCartItems } from "../context/CartContext"; // ✅ Import getCartItems

const CartWidget = () => {
    const [cartCount, setCartCount] = useState(0);

    // ✅ Function to update cart count
    const updateCartCount = () => {
        const totalItems = getCartItems().reduce((total, item) => total + item.quantity, 0);
        setCartCount(totalItems);
    };

    return (
        <div onClick={updateCartCount} style={{ cursor: "pointer" }}>
            <i className="fas fa-shopping-cart"></i>
            <span className="badge bg-secondary">{cartCount}</span> {/* ✅ Updates total quantity */}
        </div>
    );
};

export default CartWidget;
