import React from "react";
import coffeeShops from "../data/coffeeShops";
import { useCart } from "../context/CartContext"; // ✅ Use the new `useCart()`

const Catalog = () => {
    const { addItem } = useCart(); // ✅ Get `addItem` from context

    return (
        <div>
            <h2 className="catalog-title">Coffee Catalog</h2>
            <div className="catalog">
                {coffeeShops.map((shop) => (
                    <div key={shop.id} className="card">
                        <img src={shop.image} alt={shop.name} />
                        <h3>{shop.name}</h3>
                        <p>{shop.city}</p>
                        <p>${shop.price}</p>
                        <button onClick={() => addItem(shop, 1)}>Add to Cart</button> {/* ✅ Uses `addItem` correctly */}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Catalog;
