import React from 'react';
import { useCart } from '../context/CartContext';

const CartWidget = () => {
    const { cartItems } = useCart();

    console.log("Cart Items: ", cartItems);  // Check the current cart items

    const totalAmount = cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);

    console.log("Total Amount: ", totalAmount);  // Check the computed total amount

    return (
        <div>
            <h4>Cart</h4>
            {cartItems.length > 0 ? (
                <div>
                    {cartItems.map(item => (
                        <div key={item.id}>
                            <p>{item.name}: {item.quantity} x ${item.price.toFixed(2)}</p>
                        </div>
                    ))}
                    <p>Total: ${totalAmount.toFixed(2)}</p>
                    <button onClick={() => console.log('Proceed to Checkout')}>Go to Checkout</button>
                </div>
            ) : (
                <p>Your cart is empty</p>
            )}
        </div>
    );
};

export default CartWidget;
