import React, { useState } from 'react';
import coffeeShops from './coffeeShops';

const Home = () => {
  // Create a local state to handle quantities for each coffee shop
  const [quantities, setQuantities] = useState(coffeeShops.reduce((acc, shop) => {
    acc[shop.id] = 1; // Initialize each quantity with 1
    return acc;
  }, {}));

  // Function to handle quantity changes
  const handleQuantityChange = (id, delta) => {
    setQuantities((prev) => ({
      ...prev,
      [id]: Math.max(1, prev[id] + delta) // Ensure the quantity is at least 1
    }));
  };

  // Placeholder function to simulate adding to cart
  const addToCart = (shop) => {
    console.log(`Added ${quantities[shop.id]} of ${shop.name} to the cart.`);
    // Here you would typically add to a global cart state or to local storage
  };

  return (
    <div>
      <h1>Welcome to the Coffee Experience Catalog</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around' }}>
        {coffeeShops.map((shop) => (
          <div key={shop.id} style={{ margin: '20px', border: '1px solid #ccc', padding: '10px', width: '23%' }}>
            <img src={shop.image} alt={shop.name} style={{ width: '100%', height: '200px', objectFit: 'cover' }} />
            <h2>{shop.name}</h2>
            <p>{shop.description}</p>
            <p><strong>Price: ${shop.price}</strong></p>
            <div style={{ marginTop: '10px', display: 'flex', alignItems: 'center' }}>
              <button onClick={() => handleQuantityChange(shop.id, -1)}>-</button>
              <input type="text" value={quantities[shop.id]} readOnly style={{ width: '40px', textAlign: 'center' }} />
              <button onClick={() => handleQuantityChange(shop.id, 1)}>+</button>
            </div>
            <button style={{ marginTop: '10px' }} onClick={() => addToCart(shop)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
