import { createContext, useState } from "react";

export const CartContext = createContext();

const cartState = {
  cartItems: [],
  addItem: (item, quantity) => {
    const existingItem = cartState.cartItems.find(cartItem => cartItem.id === item.id);

    if (existingItem) {
      // Update quantity if item exists
      existingItem.quantity += quantity;
    } else {
      // Add new item
      cartState.cartItems.push({ ...item, quantity });
    }
  }
};

export default cartState; // ✅ Exporting the cartState object
